import create from 'zustand'
import axios from 'axios'
import { IssuesListForRepoResponseData } from '@octokit/types'

export const BASE_URL = 'https://api.github.com/repos/cosmos/cosmos-sdk'

export type Issue = IssuesListForRepoResponseData[0]

type StoreState = {
  issuesByNum: {
    [num: string]: Issue
  }
  fetchIssues: () => void
}

export const useStore = create<StoreState>((set) => ({
  issues: [],
  issuesByNum: {},
  fetchIssues: async () => {
    try {
      // const { data: issues } = await axios.get(`${BASE_URL}/issues`)
      const issues = IssuesStubs
      issues.forEach((issue: Issue) => {
        set((state) => ({
          issuesByNum: { ...state.issuesByNum, [issue.number]: issue },
        }))
      })
    } catch (err) {
      alert(`Error fetching data from Cosmos: ${JSON.stringify(err)}`)
    }
  },
}))

